function center_arrange_seatgroups() {
	var temp_previous_cx = 0;
	var temp_previous_x = 0;
	$('.seatgroup').each(function(i, el) {
		console.log($(el).attr('id'));
		var seatgroup = SVG.select('#' + $(el).attr('id')).members[0];
		if (temp_previous_cx != 0) {
			seatgroup.x(seatgroup.x() + (temp_previous_cx - seatgroup.cx()));
		}
		console.log("temp_previous_cx: " + temp_previous_cx);
		temp_previous_cx = seatgroup.cx();
	})
}


