var seat_rows_names=["A", "B", "C", "D", "E", "F", "G", "H","I", "J", "K", "L","M", "N", "O"];
var select_row_seats=function(rowid) {
    var sarray = $('#'+rowid).find('.template_item.seat');
    // Go to the middle 
    if(sarray.length % 2 == 0){
        middle = Math.ceil((sarray.length - 2) / 2)
    }else{
        middle = Math.ceil((sarray.length - 1) / 2)
    }
    aeseatMap.select_seat('#' + $(sarray[middle]).attr('id'))
    for(i = 1; i < Math.ceil(sarray.length / 2) + 1; i++){
        if(sarray[middle + i]){
            aeseatMap.select_seat('#' + $(sarray[middle + i]).attr('id'))
        }
        if(sarray[middle - i]){
            aeseatMap.select_seat('#' + $(sarray[middle - i]).attr('id'))
        }
    }
}
function assign_row_seats(rowid,rowname){
	aeseatMap.deselect_all();
	$('#seatmaper_namer_row').val(rowname); 
	select_row_seats(rowid);
	aeseatMap.assign_seatnames();
	aeseatMap.deselect_all();
}

