function turn_verticle(SeatGroupParent){
    if(SeatGroupParent){
        const parent = document.querySelector(`.${SeatGroupParent}`);
        var seatGroups = parent.querySelectorAll(`.seatgroup`);
    }else{
        var seatGroups = document.querySelectorAll(`.seatgroup`);
    }

        // Loop through each seatgroup element
        seatGroups.forEach((seatGroup,index) => {      
        // Get the current transform property
        var currentTransform = seatGroups[index].getAttribute("transform");
        if(index > 0){
        var prevTransform = seatGroups[index - 1].getAttribute("transform");   
        const prevmatrixValues = prevTransform.replace(/matrix\((.+)\)/, "$1").split(","); 
        const prevmatrixArray = prevmatrixValues.map(parseFloat);
        new_x = prevmatrixArray[4] + 50
        new_y = prevmatrixArray[5]
        var newarr = [
            0,1,-1,0,new_x,new_y
        ]
        var newmatrix = `matrix(${newarr.join(",")})`;
        seatGroup.setAttribute("transform", newmatrix);
        }else{
        const matrixValues = currentTransform.replace(/matrix\((.+)\)/, "$1").split(",");

        // Convert the matrix values into an array of numbers
        const matrixArray = matrixValues.map(parseFloat);

        new_x = matrixArray[4]
        new_y = matrixArray[5]
        var newarr = [
            0,1,-1,0,new_x,new_y
        ]
        var newmatrix = `matrix(${newarr.join(",")})`;
        seatGroup.setAttribute("transform", newmatrix);
        }
        });
}

